    #[tokio::test]
    async fn test_map_children_block_domains() {
        use crate::types::{Context, CTX};
        use std::sync::Arc;

        let html = r#"
            <html><body>
                <article>
                    <div><h3>Example 1</h3><p><a href="https://example.com/1">View</a></p></div>
                    <div><h3>Example 2</h3><p><a href="https://example.com/2">Share</a></p></div>
                    <div><h3>Example 3</h3><p><a href="https://example.com/3">View</a></p></div>
                    <div><h3>Example 4</h3><p><a href="https://reddit.com/example">View</a></p></div>
                </article>
            </body></html>
        "#;

        let ctx = Arc::new(Context::new().with_block_domains(&["reddit.com"]));
        let urls = CTX
            .scope(ctx, async move {
                map_children(html, "https://example.com").await
            })
            .await;

        // Should exclude sibling with reddit link
        assert_eq!(urls.len(), 3);
        assert!(urls.contains(&"https://example.com/1".to_string()));
        assert!(urls.contains(&"https://example.com/2".to_string()));
        assert!(urls.contains(&"https://example.com/3".to_string()));
        assert!(!urls.iter().any(|u| u.contains("reddit")));
    }

    #[tokio::test]
    async fn test_map_children_allow_domains() {
        use crate::types::{Context, CTX};
        use std::sync::Arc;

        let html = r#"
            <html><body>
                <article>
                    <div><h3>Job 1</h3><p><a href="https://stripe.com/jobs/1">Apply</a></p></div>
                    <div><h3>Job 2</h3><p><a href="https://other.com/job">Apply</a></p></div>
                    <div><h3>Job 3</h3><p><a href="https://openai.com/jobs/3">Apply</a></p></div>
                    <div><h3>Job 4</h3><p><a href="https://openai.com/jobs/4">Apply</a></p></div>
                    <div><h3>Job 5</h3><p><a href="https://openai.com/jobs/5">Apply</a></p></div>
                </article>
            </body></html>
        "#;

        let ctx =
            Arc::new(Context::new().with_allow_domains(&["stripe.com", "openai.com"]));
        let urls = CTX
            .scope(ctx, async move {
                map_children(html, "https://example.com").await
            })
            .await;

        // Should only include stripe and openai links
        assert_eq!(urls.len(), 4);
        assert!(urls.iter().any(|u| u.contains("stripe.com")));
        assert!(urls.iter().any(|u| u.contains("openai.com")));
        assert!(!urls.iter().any(|u| u.contains("other.com")));
    }

